package stella;import java.awt.*;import java.awt.image.*;import java.io.*;import javax.imageio.*;import javax.swing.*;public class Painting extends JComponent {	BufferedImage img;	public Painting(String filename) {		try {			img = ImageIO.read(ClassLoader.getSystemResource(filename));		} catch (IOException e) {			JOptionPane.showMessageDialog(this, "Sorry! File '" + filename + "' could not be found or opened.");			System.exit(0);		}	}	public void paint(Graphics g) {		g.drawImage(img, 0, 0, null);	}		public BufferedImage getBufferedImage() {		return img;	}	public Dimension getPreferredSize() {		if (img == null) {			return new Dimension(100,100);		} else {			return new Dimension(img.getWidth(null), img.getHeight(null));		}	}}